{
  "properties": {
    "mode": "Incremental",
    "template": {
      "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
      "contentVersion": "1.0.0.0",
      "parameters": {
        "nsgName": {
          "defaultValue": "{{nsg_name}}",
          "type": "string",
          "metadata": {
            "description": "The name of the network security group to create."
          }
        },
        "vnetName": {
          "defaultValue": "{{vnet_name}}",
          "type": "string",
          "metadata": {
            "description": "The name of the virtual network to create."
          }
        },
        "workspaceName": {
          "defaultValue": "{{workspace_name}}",
          "type": "string",
          "metadata": {
            "description": "The name of the Azure Databricks workspace to create."
          }
        },
        "privateSubnetName": {
          "defaultValue": "{{private_subnet_name}}",
          "type": "string",
          "metadata": {
            "description": "The name of the private subnet to create."
          }
        },
        "publicSubnetName": {
          "defaultValue": "{{public_subnet_name}}",
          "type": "string",
          "metadata": {
            "description": "The name of the public subnet to create."
          }
        },
        "pricingTier": {
          "defaultValue": "{{pricing_tier}}",
          "allowedValues": [
            "trial",
            "standard",
            "premium"
          ],
          "type": "string",
          "metadata": {
            "description": "The pricing tier of workspace."
          }
        },
        "location": {
          "type": "string",
          "defaultValue": "[resourceGroup().location]",
          "metadata": {
            "description": "Location for all resources."
          }
        },
        "vnetCidr": {
          "defaultValue": "{{vnet_cidr}}",
          "type": "string",
          "metadata": {
            "description": "Cidr range for the vnet."
          }
        },
        "privateSubnetCidr": {
          "defaultValue": "{{private_subnet_cidr}}",
          "type": "string",
          "metadata": {
            "description": "Cidr range for the private subnet."
          }
        },
        "publicSubnetCidr": {
          "defaultValue": "{{public_subnet_cidr}}",
          "type": "string",
          "metadata": {
            "description": "Cidr range for the public subnet.."
          }
        },
        "enableNoPublicIp": {
          "defaultValue": "[bool('{{enable_no_public_ip}}')]",
          "type": "bool"
        }
      },
      "variables": {
        "nsgId": "[resourceId('Microsoft.Network/networkSecurityGroups', parameters('nsgName'))]",
        "vnetId": "[resourceId('Microsoft.Network/virtualNetworks', parameters('vnetName'))]",
        "managedResourceGroupId": "[concat(subscription().id, '/resourceGroups/', variables('managedResourceGroupName'))]",
        "managedResourceGroupName": "[concat('databricks-rg-', parameters('workspaceName'), '-', uniqueString(parameters('workspaceName'), resourceGroup().id))]"
      },
      "resources": [
        {
          "apiVersion": "2019-06-01",
          "type": "Microsoft.Network/networkSecurityGroups",
          "location": "[parameters('location')]",
          "name": "[parameters('nsgName')]"
        },
        {
          "apiVersion": "2019-06-01",
          "type": "Microsoft.Network/virtualNetworks",
          "location": "[parameters('location')]",
          "name": "[parameters('vnetName')]",
          "dependsOn": [
            "[concat('Microsoft.Network/networkSecurityGroups/', parameters('nsgName'))]"
          ],
          "properties": {
            "addressSpace": {
              "addressPrefixes": [
                "[parameters('vnetCidr')]"
              ]
            },
            "subnets": [
              {
                "name": "[parameters('publicSubnetName')]",
                "properties": {
                  "addressPrefix": "[parameters('publicSubnetCidr')]",
                  "networkSecurityGroup": {
                    "id": "[variables('nsgId')]"
                  },
                  "delegations": [
                    {
                      "name": "databricks-del-public",
                      "properties": {
                        "serviceName": "Microsoft.Databricks/workspaces"
                      }
                    }
                  ]
                }
              },
              {
                "name": "[parameters('privateSubnetName')]",
                "properties": {
                  "addressPrefix": "[parameters('privateSubnetCidr')]",
                  "networkSecurityGroup": {
                    "id": "[variables('nsgId')]"
                  },
                  "delegations": [
                    {
                      "name": "databricks-del-private",
                      "properties": {
                        "serviceName": "Microsoft.Databricks/workspaces"
                      }
                    }
                  ]
                }
              }
            ]
          }
        },
        {
          "apiVersion": "2018-04-01",
          "type": "Microsoft.Databricks/workspaces",
          "location": "[parameters('location')]",
          "name": "[parameters('workspaceName')]",
          "dependsOn": [
            "[concat('Microsoft.Network/networkSecurityGroups/', parameters('nsgName'))]",
            "[concat('Microsoft.Network/virtualNetworks/', parameters('vnetName'))]"
          ],
          "sku": {
            "name": "[parameters('pricingTier')]"
          },
          "comments": "The managed resource group specified will be locked after deployment.",
          "properties": {
            "ManagedResourceGroupId": "[variables('managedResourceGroupId')]",
            "parameters": {
              "customVirtualNetworkId": {
                "value": "[variables('vnetId')]"
              },
              "customPublicSubnetName": {
                "value": "[parameters('publicSubnetName')]"
              },
              "customPrivateSubnetName": {
                "value": "[parameters('privateSubnetName')]"
              },
              "enableNoPublicIp": {
                "value": "[parameters('enableNoPublicIp')]"
              }
            }
          }
        }
      ]
    }
  }
}
