{
   "properties": {
   "mode": "Incremental",
   "template": {
    "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
    "contentVersion": "1.0.0.0",
    "parameters": {
        "nsgName": {
            "defaultValue": "databricks-{{databricks_workspace_name}}-nsg",
            "type": "string",
            "metadata": {
                "description": "The name of the network security group to create."
            }
        },
        "vnetName": {
            "defaultValue": "databricks-vnet",
            "type": "string",
            "metadata": {
                "description": "The name of the virtual network to create."
            }
        },
        "workspaceName": {
            "defaultValue": "test12",
            "type": "string",
            "metadata": {
                "description": "The name of the Azure Databricks workspace to create."
            }
        },
        "privateSubnetName": {
            "defaultValue": "container-subnet",
            "type": "string",
            "metadata": {
                "description": "The name of the private subnet to create."
            }
        },
        "publicSubnetName": {
            "defaultValue": "host-subnet",
            "type": "string",
            "metadata": {
                "description": "The name of the public subnet to create."
            }
        },
        "pricingTier": {
            "defaultValue": "premium",
            "allowedValues": [
                "trial",
                "standard",
                "premium"
            ],
            "type": "string",
            "metadata": {
                "description": "The pricing tier of workspace."
            }
        },
        "location": {
            "type": "string",
            "defaultValue": "[resourceGroup().location]",
            "metadata": {
                "description": "Location for all resources."
            }
        },
        "vnetCidr": {
            "defaultValue": "11.141.13.0/22",
            "type": "string",
            "metadata": {
                "description": "Cidr range for the vnet."
            }
        },
        "publicSubnetCidr": {
            "defaultValue": "11.141.13.64/26",
            "type": "string",
            "metadata": {
                "description": "Cidr range for the public subnet.."
            }
        },
        "privateSubnetCidr": {
            "defaultValue": "11.141.13.128/26",
            "type": "string",
            "metadata": {
                "description": "Cidr range for the private subnet."
            }
        },
        "enableNoPublicIp": {
            "defaultValue": "[bool('false')]",
            "type": "bool"
        }
    },
    "variables": {
        "azureRegionToControlPlaneIp": {
            "centralus": "23.101.152.95/32",
            "eastus": "23.101.152.95/32",
            "eastus2": "23.101.152.95/32",
            "northcentralus": "23.101.152.95/32",
            "southcentralus": "40.83.178.242/32",
            "westcentralus": "40.83.178.242/32",
            "westus": "40.83.178.242/32",
            "westus2": "40.83.178.242/32"
        },
        "controlPlaneIp": "[variables('azureRegionToControlPlaneIp')[parameters('location')]]",
        "azureRegionToWebappIp": {
            "centralus": "40.70.58.221/32",
            "eastus": "40.70.58.221/32",
            "eastus2": "40.70.58.221/32",
            "northcentralus": "40.70.58.221/32",
            "southcentralus": "40.118.174.12/32",
            "westcentralus": "40.118.174.12/32",
            "westus": "40.118.174.12/32",
            "westus2": "40.118.174.12/32"
        },
        "webappIp": "[variables('azureRegionToWebappIp')[parameters('location')]]",
        "nsgId": "[resourceId('Microsoft.Network/networkSecurityGroups', parameters('nsgName'))]",
        "vnetId": "[resourceId('Microsoft.Network/virtualNetworks', parameters('vnetName'))]",
        "managedResourceGroupId": "[concat(subscription().id, '/resourceGroups/', variables('managedResourceGroupName'))]",
        "managedResourceGroupName": "[concat('databricks-rg-', parameters('workspaceName'), '-', uniqueString(parameters('workspaceName'), resourceGroup().id))]"
    },
    "resources": [
        {
            "apiVersion": "2018-02-01",
            "type": "Microsoft.Network/networkSecurityGroups",
            "location": "[parameters('location')]",
            "name": "[parameters('nsgName')]",
            "properties": {
                "securityRules": [
                    {
                        "name": "databricks-worker-to-worker-inbound",
                        "properties": {
                            "access": "Allow",
                            "description": "Required for worker nodes communication within a cluster.",
                            "destinationAddressPrefix": "*",
                            "destinationPortRange": "*",
                            "direction": "Inbound",
                            "priority": 200,
                            "protocol": "*",
                            "sourceAddressPrefix": "VirtualNetwork",
                            "sourcePortRange": "*"
                        }
                    },
                    {
                        "name": "databricks-control-plane-ssh",
                        "properties": {
                            "access": "Allow",
                            "description": "Required for Databricks control plane management of worker nodes.",
                            "destinationAddressPrefix": "*",
                            "destinationPortRange": "22",
                            "direction": "Inbound",
                            "priority": 100,
                            "protocol": "*",
                            "sourceAddressPrefix": "[variables('controlPlaneIp')]",
                            "sourcePortRange": "*"
                        }
                    },
                    {
                        "name": "databricks-control-plane-worker-proxy",
                        "properties": {
                            "access": "Allow",
                            "description": "Required for Databricks control plane communication with worker nodes.",
                            "destinationAddressPrefix": "*",
                            "destinationPortRange": "5557",
                            "direction": "Inbound",
                            "priority": 110,
                            "protocol": "*",
                            "sourceAddressPrefix": "[variables('controlPlaneIp')]",
                            "sourcePortRange": "*"
                        }
                    },
                    {
                        "name": "databricks-worker-to-webapp",
                        "properties": {
                            "access": "Allow",
                            "description": "Required for workers communication with Databricks Webapp.",
                            "destinationAddressPrefix": "[variables('webappIp')]",
                            "destinationPortRange": "*",
                            "direction": "Outbound",
                            "priority": 100,
                            "protocol": "*",
                            "sourceAddressPrefix": "*",
                            "sourcePortRange": "*"
                        }
                    },
                    {
                        "name": "databricks-worker-to-sql",
                        "properties": {
                            "access": "Allow",
                            "description": "Required for workers communication with Azure SQL services.",
                            "destinationAddressPrefix": "Sql",
                            "destinationPortRange": "*",
                            "direction": "Outbound",
                            "priority": 110,
                            "protocol": "*",
                            "sourceAddressPrefix": "*",
                            "sourcePortRange": "*"
                        }
                    },
                    {
                        "name": "databricks-worker-to-storage",
                        "properties": {
                            "access": "Allow",
                            "description": "Required for workers communication with Azure Storage services.",
                            "destinationAddressPrefix": "Storage",
                            "destinationPortRange": "*",
                            "direction": "Outbound",
                            "priority": 120,
                            "protocol": "*",
                            "sourceAddressPrefix": "*",
                            "sourcePortRange": "*"
                        }
                    },
                    {
                        "name": "databricks-worker-to-worker-outbound",
                        "properties": {
                            "access": "Allow",
                            "description": "Required for worker nodes communication within a cluster.",
                            "destinationAddressPrefix": "VirtualNetwork",
                            "destinationPortRange": "*",
                            "direction": "Outbound",
                            "priority": 130,
                            "protocol": "*",
                            "sourceAddressPrefix": "*",
                            "sourcePortRange": "*"
                        }
                    },
                    {
                        "name": "databricks-worker-to-any",
                        "properties": {
                            "access": "Allow",
                            "description": "Required for worker nodes communication with any destination.",
                            "destinationAddressPrefix": "*",
                            "destinationPortRange": "*",
                            "direction": "Outbound",
                            "priority": 140,
                            "protocol": "*",
                            "sourceAddressPrefix": "*",
                            "sourcePortRange": "*"
                        }
                    }
                ]
            }
        },
        {
            "apiVersion": "2018-02-01",
            "type": "Microsoft.Network/virtualNetworks",
            "location": "[parameters('location')]",
            "name": "[parameters('vnetName')]",
            "dependsOn": [
                "[concat('Microsoft.Network/networkSecurityGroups/', parameters('nsgName'))]"
            ],
            "properties": {
                "addressSpace": {
                    "addressPrefixes": [
                        "[parameters('vnetCidr')]"
                    ]
                },
                "subnets": [
                    {
                        "name": "[parameters('publicSubnetName')]",
                        "properties": {
                            "addressPrefix": "[parameters('publicSubnetCidr')]",
                            "networkSecurityGroup": {
                                "id": "[variables('nsgId')]"
                            }
                        }
                    },
                    {
                        "name": "[parameters('privateSubnetName')]",
                        "properties": {
                            "addressPrefix": "[parameters('privateSubnetCidr')]",
                            "networkSecurityGroup": {
                                "id": "[variables('nsgId')]"
                            }
                        }
                    }
                ]
            }
        },
        {
            "apiVersion": "2018-04-01",
            "type": "Microsoft.Databricks/workspaces",
            "location": "[parameters('location')]",
            "name": "[parameters('workspaceName')]",
            "dependsOn": [
                "[concat('Microsoft.Network/networkSecurityGroups/', parameters('nsgName'))]",
                "[concat('Microsoft.Network/virtualNetworks/', parameters('vnetName'))]"
            ],
            "sku": {
                "name": "[parameters('pricingTier')]"
            },
            "comments": "The resource group specified will be locked after deployment.",
            "properties": {
                "ManagedResourceGroupId": "[variables('managedResourceGroupId')]",
                "parameters": {
                    "customVirtualNetworkId": {
                        "value": "[variables('vnetId')]"
                    },
                    "customPublicSubnetName": {
                        "value": "[parameters('publicSubnetName')]"
                    },
                    "customPrivateSubnetName": {
                        "value": "[parameters('privateSubnetName')]"
                    },
                    "enableNoPublicIp": {
                        "value": "[parameters('enableNoPublicIp')]"
                    }
                }
            }
        }
    ]
},
   "parameters": {
     "workspaceName": {
       "value": "{{databricks_workspace_name}}"
     },
     "pricingTier":{
       "value":"{{databricks_pricing_tier}}"
     },
     "nsgName":{
       "value":"{{databricks_nsg_name}}"
     },
     "vnetName":{
       "value":"{{databricks_vnet_name}}"
     },
     "vnetCidr":{
       "value":"{{databricks_vnet_cidr}}"
     },
     "privateSubnetName":{
       "value":"{{databricks_container_subnet_name}}"
     },
     "publicSubnetName":{
       "value":"{{databricks_host_subnet_name}}"
     },
     "privateSubnetCidr":{
       "value":"{{databricks_container_subnet_cidr}}"
     },
     "publicSubnetCidr":{
       "value":"{{databricks_host_subnet_cidr}}"
     }
   }
 }
}

